import React,{useEffect,useState} from 'react';
import { useHistory } from "react-router-dom";
import './Register.css'


function Register({setLog,log,onLogin}) {
    const history = useHistory();
    const [name,setName] =useState("")
    const [phoneNumber,setPhoneNumber] =useState()
    const [email,setEmail] =useState("")
    const [isLoading,setIsLoading] =useState(false)
    const [houseNumber,setHouseNumber] =useState("")
    function handleSubmit(e){
        e.preventDefault()
        const newUser={
            name: name,
            Phone: phoneNumber,
            email: email,
            house_id: houseNumber
        }

            fetch('http://localhost:3000/users',{
                method:'POST',
                headers: {
                    "Content-Type":"application/json",
                },
                body: JSON.stringify(newUser)
            })
            .then((r)=>{
                setIsLoading(false);
                if (r.ok) {
                    r.json().then((user)=>{
                        onLogin(user);
                        history.push('/home')
                    });
                }
            })

    }   
    return (
    <form action="action_page.php" className='login' onSubmit={handleSubmit}>
        <div className="form-group">
            <label>First Name :</label>
            <input
            required 
            type="text"
            className="form-control" 
            placeholder="Enter Your Name"
            value={name}
            onChange={(e)=>setName(e.target.value)}
            />
        </div>
        <div className="form-group">
            <label >Email :</label>
            <input
            required
            type="email" className="form-control" 
            value={email}
            onChange={(e)=>setEmail(e.target.value)}
            placeholder="mike@gmail.com" important/>
        </div>
        <div className="form-group">
            <label>House_id : </label>
            <input
            required
            type="number" className="form-control" id="exampleInputEmail1"  placeholder="1"
            value={houseNumber}
            onChange={(e)=>setHouseNumber(e.target.value)}
            />
            <div className='info'><p>Type 1 if not sure</p></div>
        </div>


        <button type="submit" className="btn btn-primary">{isLoading ? "Loading..." : "Sign Up"}</button>
    </form>
    )
}

export default Register
