import React,{useEffect,useState} from 'react'
import './Register.css'


function Register() {
    const [name,setName] =useState("")
    const [phoneNumber,setPhoneNumber] =useState("")
    const [email,setEmail] =useState("")
    const [houseNumber,setHouseNumber] =useState("")
    function handleSubmit(e){
        e.preventDefault()
        const newUser={
            name: name,
            Phone: phoneNumber,
            email: email,
            house_id: houseNumber
        }

            fetch('/users',{
                method:'POST',
                headers: {
                    "Content-Type":"application/json",
                },
                body: JSON.stringify(newUser)
            })
            .then((res) => res.json())
            .then((data) => {
            console.log(data);
        });
    }   
    return (
    <form action="action_page.php" className='login' onSubmit={handleSubmit}>
        <div class="form-group">
            <label for="exampleInputEmail1">First Name :</label>
            <input 
            type="text"
            class="form-control" 
            placeholder="Enter Your Name"
            value={name}
            onChange={(e)=>setName(e.target.value)}
            />
        </div>
        <div class="form-group">
            <label for="exampleInputEmail1">House_id : </label>
            <input type="number" class="form-control" id="exampleInputEmail1"  placeholder="1"/>
            <div className='info'><p>Type 1 if not sure</p></div>
        </div>
        <div class="form-group">
            <label for="exampleInputPassword1">Email :</label>
            <input type="email" class="form-control" id="exampleInputPassword1" placeholder="mike@gmail.com"/>
        </div>
        <div class="form-group">
            <label for="exampleInputEmail1">Phone Number : </label>
            <input type="number" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="+25471234"/>
        </div>

        <button type="submit" class="btn btn-primary">Submit</button>
    </form>
    )
}

export default Register
